    public void onViewCreated(@NonNull View view, Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

      RecyclerView shoppingList = view.findViewById(R.id.shoppingList);
      shoppingList.setLayoutManager(new GridLayoutManager(getActivity(),2));
      shoppingAdapter adapter=new shoppingAdapter(getActivity(),R.layout.card_shopping,ShoppingApp);
      shoppingList.setAdapter(adapter);


    }
    class shoppingAdapter extends RecyclerView.Adapter<shoppingAdapter.Holder>
    {

        private final Context c;
        private final int layout;
        private final List<shopping> shoppings;
        private final LayoutInflater inflater;


        public shoppingAdapter(Context c, int layout, List<shopping> shoppings) {
            this.c = c;
            this.layout = layout;
            this.shoppings = shoppings;
            inflater = LayoutInflater.from(c);
        }

        @NonNull
        @Override
        public Holder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
            View v=inflater.inflate(layout,parent,false);

            return new Holder(v);
        }

        @Override
        public void onBindViewHolder(@NonNull Holder holder, int position) {
             shopping shoppingApp = shoppings.get(position); // get a hero object from list using position
            holder.title.setText(shoppingApp.title); //set the title from hero object
            Glide.with(c).load(shoppingApp.imageUrl).into(holder.image);
            holder.card.setTag(shoppingApp);

        }

        @Override
        public int getItemCount() {
            return shoppings.size();
        }

        class Holder extends RecyclerView.ViewHolder{

            CardView card;
            TextView title;
            ImageView image;
            public Holder(@NonNull View v) {
                super(v);
                card=v.findViewById((R.id.card));
                title=v.findViewById((R.id.title));
                image=v.findViewById((R.id.image));

                card.setOnClickListener(new View.OnClickListener() {
                    @Override
                    public void onClick(View view) {
                        shopping shoppingApp=(shopping) view.getTag();
                        FirstFragmentDirections.ActionFirstFragmentToSecondFragment action = FirstFragmentDirections.actionFirstFragmentToSecondFragment();
                        action.setTitle(shoppingApp.title);
                        action.setDescription(shoppingApp.description);
                        action.setImage(shoppingApp.imageUrl);
                        Navigation.findNavController(view).navigate(action);


                    }
                });
            }
        }
    }
